{-# LANGUAGE LambdaCase #-}

module Day19 where

import Data.List.Split
import Data.Map.Strict (Map,(!))
import qualified Data.Map.Strict as M
import Data.Set (Set)
import qualified Data.Set as S
import Data.Maybe

type Coord = (Int,Int)
type CoordMap = Map Coord Piece

data Direction = U | D | L | R
  deriving (Eq,Show,Ord)

data Piece = UD | LR | PL | Letter Char
  deriving (Eq,Show)

part1 = getLetters $ last $ doRoute m
  where 
    getLetters (p,cs,_) = concatMap (getLtr . (m !)) $ tail $ reverse (p:cs)
    m = parseInput input

part2 = countSteps $ last $ doRoute m
  where 
    countSteps (_,cs,_) = length cs
    m = parseInput input

doRoute :: CoordMap -> [(Coord, [Coord], Set (Coord, Direction))]
doRoute m = takeWhile (\(_,s,_) -> numLtrs s < totalLtrs) $ iterate (step m) (start, [(xi,-1)], S.empty)
  where
    numLtrs = length . filter isLtr . mapMaybe (`M.lookup` m)
    totalLtrs = M.size $ M.filter isLtr m
    start@(xi,_) = head $ filter (\(_,y) -> y == 0) $ M.keys m

isLtr :: Piece -> Bool
isLtr (Letter _) = True
isLtr _ = False

getLtr :: Piece -> String
getLtr (Letter c) = [c]
getLtr _ = ""

step :: CoordMap -> (Coord,[Coord],Set (Coord,Direction)) -> (Coord,[Coord],Set (Coord,Direction))
step m (curr,lastSeen:ls,seen) = (curr',curr:lastSeen:ls,seen')
  where 
    curr'
      | canKeepGoing = inSameDir
      | otherwise = head ns
    seen' = S.insert (curr, dir lastSeen curr) seen
    inSameDir = move curr $ dir lastSeen curr
    canKeepGoing = inSameDir `elem` ns
    backward = oppositeDir (dir lastSeen curr) . dir curr
    ns = filter (\c -> (c,dir curr c) `S.notMember` seen && c `M.member` m && not (backward c)) $ neighbors curr

oppositeDir :: Direction -> Direction -> Bool
oppositeDir U D = True
oppositeDir D U = True
oppositeDir L R = True
oppositeDir R L = True
oppositeDir _ _ = False

move :: Coord -> Direction -> Coord
move (x,y) = \case
  U -> (x,y-1)
  D -> (x,y+1)
  L -> (x-1,y)
  R -> (x+1,y)

dir :: Coord -> Coord -> Direction
dir (x,y) (x',y')
    | y < y' = D
    | y > y' = U
    | x < x' = R
    | x > x' = L

neighbors :: Coord -> [Coord]
neighbors (x,y) = [(x+1,y),(x-1,y),(x,y+1),(x,y-1)]

parseInput :: String -> CoordMap
parseInput s = M.fromList $ map (\(c,m) -> (c,fromJust m)) $ filter (isJust . snd) allCoords
  where
    allCoords = concat $ zipWith (\y l -> zipWith (\x c -> ((x,y),c)) [0..length l] l) [0..(length orderedPieces)] orderedPieces 
    orderedPieces = map (map p) $ splitOn "\n" s
    p '|' = Just UD
    p '-' = Just LR
    p '+' = Just PL
    p ' ' = Nothing
    p c = Just $ Letter c

