module Day8 where

import Text.Trifecta
import Control.Applicative
import Data.Map.Strict (Map, (!))
import qualified Data.Map.Strict as M
import Data.Maybe
import Data.List
import Data.Function

type Reg = String

data Op = Order Ordering | NE | LE | GE
    deriving (Eq,Show)
data InstType = Inc | Dec
    deriving (Eq,Show)
data Inst = Inst Reg InstType Integer Reg Op Integer
    deriving (Eq,Show)

part1 = maxmimumVal $ foldl' evalInst M.empty $ m
    where (Success m) = parseInput input
part2 = fst $ foldl' foo (0,M.empty) $ m
    where
        foo (mx,m) i = (mx',e)
            where
                mx' = if maxmimumVal e > mx then maxmimumVal e else mx 
                e = evalInst m i 
        (Success m) = parseInput input

maxmimumVal :: Map Reg Integer -> Integer
maxmimumVal = snd . maximumBy (compare `on` snd) . M.toList

parseInput :: String -> Result [Inst]
parseInput = parseString (some (instParser <* skipOptional newline)) mempty

evalOp :: Map String Integer -> Reg -> Op -> Integer -> Bool
evalOp m r o i = case o of
    NE -> valR2 /= i
    LE -> valR2 <= i
    GE -> valR2 >= i
    (Order EQ) -> valR2 == i
    (Order LT) -> valR2 < i
    (Order GT) -> valR2 > i
    where  
        valR2 = M.findWithDefault 0 r m

evalInst :: Map String Integer -> Inst -> Map String Integer
evalInst m (Inst r t i r2 o i2) = if (evalOp m r2 o i2) 
                                  then M.alter (\m -> Just $ evalInstType (fromMaybe 0 m) t i) r m
                                  else m
    where
        evalInstType i Dec i2 = i - i2
        evalInstType i Inc i2 = i + i2
    

instParser :: Parser Inst
instParser = (\r t i _ r2 op i2 -> Inst r t i r2 op i2) <$> token regParser <*> token instTypeParser <*> token integer <*> token (string "if") <*> token regParser  <*> token opParser <*> integer
    where 
        regParser = some alphaNum
        instTypeParser = try (Inc <$ string "inc") <|> (Dec <$ string "dec")
        opParser = try (NE <$ string "!=") 
                <|> try (Order EQ <$ string "==") 
                <|> try (LE <$ string "<=") 
                <|> try (GE <$ string ">=")
                <|> try (Order LT <$ string "<") 
                <|> (Order GT <$ string ">") 

